(148, 'Craig', '', 'Yellowlees', '', 'craig.yellowlees@usask.ca', 'I am a MSc student working under the supervision of Dr. Carl Gutwin. My current research interest is in high speed coordination in multiplayer games.', 'graduate_student', 'csa280', NULL, NULL),
(149, 'Jade', '', 'Anderson', '', 'jma809@mail.usask.ca', 'Hey everyone! I just started working for the HCI lab this September as a research assistant.  I am in my 3rd year psychology with a minor in Computer Science.  I grew up in Stockholm, Sk. where my family breeds and trains Arabian Horses.', 'alumni', 'jma809', NULL, NULL),
