(210, 'Amin', '', 'Tavassolian', 'University of Saskatchewan', 'amin.tavassolian@usask.ca', '<table style="width: 60%;">\r\n            <tr>\r\n                <td class="style1">\r\n                    &nbsp;\r\n                </td>\r\n                <td>\r\n                    I am a Master student under the co-supervision of Dr. Carl Gutwin and Dr Kevin \r\n                    G. Stanely. My main interests include gamified applications, game design and \r\n                    interface design especially for smart phones.\r\n                </td>\r\n                <td>\r\n                    &nbsp;\r\n                </td>\r\n            </tr>\r\n            <tr>\r\n                <td class="style1">\r\n                    &nbsp;\r\n                </td>\r\n                <td style="border-bottom-style: solid; font-weight: bold;">\r\n                    Publications<br />\r\n                    __________________________________________________________________</td>\r\n                <td>\r\n                    &nbsp;\r\n                </td>\r\n            </tr>\r\n            <tr>\r\n                <td class="style1">', 'graduate_student', 'amt437', NULL, NULL),
(211, 'Aryan', '', 'Zohoorian', '', 'ariyan.zohoorian@usask.ca', 'He is a Master''s student under the co-supervision of Dr. Carl Gutwin and Dr. Kevin Stanley. His current research area is in Computer Supported Cooperative Work with the focus on player coordination and interaction design. He is also a member of \r\n\r\n<a href="http://www.cs.usask.ca/research/research_groups/discus/general.html">Discus Lab</a>.\r\n\r\n\r\n<div>\r\nAreas of Expertise\r\n<ul>\r\n<li>Mobile Application Development and UI Design</li>\r\n<li>Player Coordination and Interaction Design</li>\r\n<li>Web and Graphic Design</li>\r\n</div>\r\n\r\n\r\n<div>\r\nPublications\r\n<ul>\r\n<li>Zohoorian A., Stanley K. G., Gutwin C., Tavassolian A., “PLATO: A Coordination Framework for Designers of Multi-Player Real-Time Digital Games”, appears in Proc FDG ''12.</li>\r\n<li>Zohoorian A., Stanley K. G., Gutwin C., Tavassolian A., “PLATO: A Coordination Framework for Multi-Player Real-Time Digital Games”, GRAND conference - Poster.</li>\r\n<li>Tavassolian A., Stanley K. G., Gutwin C., Zohoorian A., “Time Balancing with Adaptive ', 'graduate_student', 'arz301', NULL, NULL),
